function loadMessages(n){$.ajax({type:"GET",url:"/public/chat/buscarMensagens/"+n,dataType:"html",cache:!1,async:!0,success:function(n){$("#messagesPartialView").empty();$("#messagesPartialView").html(n)}})}function SendMessage(){$.ajax({type:"POST",url:"/public/chat/enviarMensagem/",data:{teamId:$("#teamId").val(),message:$("#message").val()},async:!0,success:function(n){var t=JSON.parse(n);$("#chat-content").append("<ul class='chat-list'><li class='message receive'><div class='media'><div class='pull-left user-avatar'><img class='media-object img-circle' src='https://s3.amazonaws.com/gamific-prd/images/logos/empresas/logo-"+t.SenderLogoId+"'><\/div><div class='media-body'><p class='media-heading'> <span>"+t.SenderName+"<\/span> <span class='time'>"+t.SendDateTime+"<\/span><\/p>"+t.Message+"<\/div><\/div><\/li><\/ul>");$("#message").val("")}})}